<?php$mm=new Memcache(); //创建对象//添加memcache服务器$mm->addServer("192.168.103.250",11211);$mm->addServer("192.168.103.76",11211);$sql = "select * from stu"; //查询学生//先尝试从memcahe获取学生信息$stulist = $mm->get(md5($sql));if(empty($stulist)){	//执行查询	$pdo = new PDO("mysql:host=localhost;dbname=lamp103","root","");	$stmt = $pdo->query($sql);	$stulist = $stmt->fetchAll(PDO::FETCH_ASSOC);	echo $sql."<br/>";	//缓存	$mm->set(md5($sql),$stulist,MEMCACHE_COMPRESSED,3600);}//输出信息foreach($stulist as $stu){  echo $stu['name'].":".$stu['sex']."<br/>";}